/**
 * 系统设置项枚举
 */
declare enum SystemConfiguration {
  /**
   * @desc 屏幕亮度
   */
  Brightness = "brightness",
  /**
   * @desc 佩戴手
   */
  WearHand = "wearHand",
  /**
   * @desc 抬腕监听开关
   */
  RaiseWristSwitch = "raiseWristSwitch",
  /**
   * @desc 抬腕监听灵敏度
   */
  RaiseWristSensitivity = "raiseWristSensitivity",
  /**
   * @desc 静音模式
   */
  SilentMode = "silentMode",
  /**
   * @desc 屏幕翻转
   */
  FlipScreen = "flipScreen"
}
