/**
 * 内置动画名称枚举
 */
declare enum TransitionAnimation {
    /**
     * @desc 左侧滑入
     */
    SlideInLeft = 'slideInLeft',
    /**
     * @desc 右侧滑出
     */
    SlideOutRight = 'slideOutRight',
    /**
     * @desc 淡入
     */
    FadeIn = 'fadeIn',
    /**
     * @desc 淡出
     */
    FadeOut = 'fadeOut',
    /**
     * @desc 无动效，瞬间显示/瞬间隐藏
     */
    None = 'none',
    /**
     * @desc 放大
     */
    ZoomIn = 'zoomIn',
    /**
     * @desc 亮屏事件
     */
    ZoomOut = 'zoomOut'
}
