/**
 * 屏幕
 * 接口声明: {"name": "blueos.hardware.display.screen"}
 */
declare module '@blueos.hardware.display.screen' {

  /**
   * @desc 获取熄屏时间
   * @version 1
   * @returns  获取熄屏时间返回结果 {number}
   */
  const getScreenOffTime: () => number


  /**
   * @desc 获取 AOD 状态  AOD：Always on Display，即不点亮整块屏幕的情况下，控制屏幕局部亮起，将一些重要的信息一直显示在屏幕上。
   * @param {num} 获取 AOD 状态入参 {number}
   * @version 1
   * @returns  获取AOD状态结果 {number}
   */
  const getAodStatus: () => number
}
