/**
 * 健康
 * 接口声明: { "name": "blueos.health.health" }
 */
declare module '@blueos.health.health' {

  /**
   * @desc 支持的所有数据类型定义
   */
  const STATISTIC_TYPES: Record<any, any>

  /**
   * @desc 支持的所有数据类型定义
   */
  const DATA_TYPES: Record<any, any>

  /**
   * @desc 	获取最近一次采样数据
   * @param {obj} 获取最近一次采样数据入参 {object}
   * @version 1
   */
  const getRecentSamples: (obj: {
  /**
   *   @desc 数据类型,可取值HEART_RATE、HEART_RATE_STEP、HEART_RATE_RESTING、STANDING、INTENSITY_SPORT、STEP_COUNT、SPO2、DISTANCE、CALORIES、STRESS、WALKING_SPEED、SLEEP_UNIT、SLEEP_STAGES、SLEEP_STATUS、ENERGY、WALKING_STATUS、SPEED
   */
  dataType: Array<string>

    /**
     * @desc 成功回调
     * @param {data} 采样数据 {object}
     */
    success?: (data: {
      /**
       *   @desc 数据类型,可取值HEART_RATE、HEART_RATE_STEP、HEART_RATE_RESTING、STANDING、INTENSITY_SPORT、STEP_COUNT、SPO2、DISTANCE、CALORIES、STRESS、WALKING_SPEED、SLEEP_UNIT、SLEEP_STAGES、SLEEP_STATUS、ENERGY、WALKING_STATUS、SPEED
     */
      dataType: number
      /**
       *   @desc 数据
       */
      data: {
        /**
         *   @desc 采样时间
         */
        timeStamp: number
        /**
         *   @desc 查询到的具体 dataType 的数据值，值类型由 dataType 决定
         */
        value: any
      }
    }) => void

    /**
     * 	@desc 失败的回调函数
     * 	@param {data} 失败回调的返回值 {any}
     *	@param {code} 失败回调的返回状态码 {number}
     */
    fail?: (data: any, code: number) => void
    /**
     * @desc 执行结束后的回调
     */
    complete?: () => void
  }) => void

  /**
   *   @desc 	监听采样数据变化
   *   @param {obj} 监听采样数据变化入参 {object}
   *   @version 1
   */
  const subscribeSample: (obj: {
    /**
     * @desc 数据类型
     */
    dataType: number

    /**
     * 	@desc 失败的回调函数
     * 	@param {data} 回调的返回值 {Array<string>}
     */
    callback: (data: Array<string>) => void
    /**
     * 	@desc 失败的回调函数
     * 	@param {data} 失败回调的返回值 {any}
     *	@param {code} 失败回调的返回状态码 {number}
     */
    fail?: (data: any, code: number) => void
  }) => void

    /**
   *   @desc 	取消采样数据变化
   *   @param {obj} 取消采样数据变化入参 {object}
   *   @version 1
   */
    const unsubscribeSample: (obj: {
      /**
       * @desc 数据类型
       */
      dataType: number
    }) => void

  /**
   *   @desc 	查询当日统计数据
   *   @param {obj} 查询当日统计数据入参 {object}
   *   @version 1
   */
  const getTodayStatistic: (obj: {
    /**
     *   @desc 数据类型
     */
    dataType: number
    /**
     *   @desc 统计的维度，不同厂商，不同的 dataType 支持的维度不一样
     */
    statisticType: string
    /**
     * @desc 成功回调
     * @param {data} 采样数据 {Array<string>}
     */
    success?: (data: Array<string>) => void

    /**
     * 	@desc 失败的回调函数
     * 	@param {data} 失败回调的返回值 {any}
     *	@param {code} 失败回调的返回状态码 {number}
     */
    fail?: (data: any, code: number) => void
    /**
     * @desc 执行结束后的回调
     */
    complete?: () => void
  }) => void

  /**
   *   @desc 	查询当日统计数据
   *   @param {obj} 查询当日统计数据入参 {object}
   *   @version 1
   */
  const getStatistic: (obj: {
    /**
     *   @desc 数据类型,可取值HEART_RATE、HEART_RATE_STEP、HEART_RATE_RESTING、STANDING、INTENSITY_SPORT、STEP_COUNT、SPO2、DISTANCE、CALORIES、STRESS、WALKING_SPEED、SLEEP_UNIT、SLEEP_STAGES、SLEEP_STATUS、ENERGY、WALKING_STATUS、SPEED
     */
    dataType: number
    /**
     *   @desc 统计的维度，部分数据类型支持统计图表数据详情
     */
    statisticType: string

    /**
     *   @desc 开始时间，在这个时间之后发生的活动，包含在这个时间段之前已经发生，但是还没有结束的活动
     */
    startTime: number

    /**
     *   @desc 结束时间，在这个时间之前发生的活动，包含正在发生但还没有完全结束的活动
     */
    endTime: number
    /**
     * @desc 成功回调
     * @param {data} 采样数据 {Array<string>}
     */
    success?: (data: Array<string>) => void

    /**
     * 	@desc 失败的回调函数
     * 	@param {data} 失败回调的返回值 {any}
     *	@param {code} 失败回调的返回状态码 {number}
     */
    fail?: (data: any, code: number) => void
    /**
     * @desc 执行结束后的回调
     */
    complete?: () => void
  }) => void

  /**
   *   @desc 	监听当日统计数据
   *   @param {obj} 监听当日统计数据入参 {object}
   *   @version 1
   */
  const subscribeTodayStatistic: (obj: {
    /**
     * @desc 统计的维度，不同厂商，不同的 dataType 支持的维度不一样
     */
    statisticType: number
    /**
     * @desc 数据类型,可取值HEART_RATE、HEART_RATE_STEP、HEART_RATE_RESTING、STANDING、INTENSITY_SPORT、STEP_COUNT、SPO2、DISTANCE、CALORIES、STRESS、WALKING_SPEED、SLEEP_UNIT、SLEEP_STAGES、SLEEP_STATUS、ENERGY、WALKING_STATUS、SPEED
     */
    dataType: number

    /**
     * 	@desc 失败的回调函数
     * 	@param {data} 回调的返回值 {Array<string>}
     */
    callback: (data: Array<string>) => void
    /**
     * 	@desc 失败的回调函数
     * 	@param {data} 失败回调的返回值 {any}
     *	@param {code} 失败回调的返回状态码 {number}
     */
    fail?: (data: any, code: number) => void
  }) => void

  /**
   *   @desc 	取消监听当日统计数据
   *   @param {obj} 取消监听当日统计数据入参 {object}
   *   @version 1
   */
  const unsubscribeTodayStatistic: (obj: {
    /**
     * @desc 数据类型,可取值HEART_RATE、HEART_RATE_STEP、HEART_RATE_RESTING、STANDING、INTENSITY_SPORT、STEP_COUNT、SPO2、DISTANCE、CALORIES、STRESS、WALKING_SPEED、SLEEP_UNIT、SLEEP_STAGES、SLEEP_STATUS、ENERGY、WALKING_STATUS、SPEED
     */
    dataType: number
  }) => void
}
